import React from 'react';
import CO2Summary from './CO2Summary'; // fixed path
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid
} from 'recharts';

function PredictionChart({ data }) {
  return (
    <div className="mt-8 bg-gray-900 p-4 rounded-xl text-white">
      <h2 className="text-xl font-semibold mb-4">ðŸ“ˆ Emission Predictions (2025â€“2050)</h2>
      
      <LineChart width={800} height={400} data={data}>
        <XAxis dataKey="year" stroke="#fff" />
        <YAxis stroke="#fff" />
        <Tooltip contentStyle={{ backgroundColor: "#1f2937", border: "none", color: "#fff" }} />
        <CartesianGrid strokeDasharray="3 3" stroke="#4b5563" />
        <Line type="monotone" dataKey="predicted_co2" stroke="#82ca9d" strokeWidth={3} />
      </LineChart>

      {/* CO2 summary explanation */}
      <CO2Summary data={data} />
    </div>
  );
}

export default PredictionChart;
