import React from 'react';

function co2explain({ data }) {
  if (!data || data.length === 0) return null;

  const co2Values = data.map(item => item.emissions || item.predicted_co2);
  const max = Math.max(...co2Values);
  const min = Math.min(...co2Values);

  let level = '';
  let description = '';
  let bg = '';

  if (max < 50) {
    level = 'ðŸŸ© Low Emitter';
    bg = 'bg-green-100';
    description = 'This country emits very low levels of COâ‚‚. It may have low industrial activity, a small population, or rely heavily on renewable energy.';
  } else if (max < 300) {
    level = 'ðŸŸ¨ Moderate Emitter';
    bg = 'bg-yellow-100';
    description = 'This country emits a moderate amount of COâ‚‚, likely from growing industries, urbanization, and transport.';
  } else if (max < 1000) {
    level = 'ðŸŸ§ High Emitter';
    bg = 'bg-orange-100';
    description = 'This country has high COâ‚‚ emissions due to industrialization, energy use, and economic activity.';
  } else {
    level = 'ðŸŸ¥ Very High Emitter';
    bg = 'bg-red-100';
    description = 'This country is one of the largest COâ‚‚ emitters globally. Heavy fossil fuel use and industrial activity drive emissions significantly.';
  }

  return (
    <div className={`p-4 mt-4 rounded-lg shadow-md ${bg}`}>
      <h3 className="text-lg font-bold">{level}</h3>
      <p className="mt-1 text-sm text-gray-700">{description}</p>
      <p className="mt-2 text-xs text-gray-500">Based on a COâ‚‚ range of {min.toFixed(2)} to {max.toFixed(2)} MtCOâ‚‚</p>
    </div>
  );
}

export default co2explain;
